import React, { useState, useRef, useEffect } from "react";
import axios from "axios";
import {
  Box,
  TextField,
  Button,
  Typography,
  Paper,
  CircularProgress,
  Avatar,
  IconButton,
  useTheme,
  createTheme,
  ThemeProvider,
  Switch,
} from "@mui/material";
import SendIcon from "@mui/icons-material/Send";
import AccountCircleIcon from "@mui/icons-material/AccountCircle";
import SmartToyIcon from "@mui/icons-material/SmartToy";
import ImageIcon from "@mui/icons-material/Image";

const darkTheme = createTheme({
  palette: {
    mode: "dark",
  },
});

const lightTheme = createTheme({
  palette: {
    mode: "light",
  },
});

const Chatbot = () => {
  const [query, setQuery] = useState("");
  const [chatHistory, setChatHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [darkMode, setDarkMode] = useState(false);
  const [isTyping, setIsTyping] = useState(false);
  const [file, setFile] = useState(null);

  const chatEndRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [chatHistory, isTyping]);

  const handleQuery = async () => {
    if (!query.trim() && !file) return;
    setLoading(true);
    setError("");
    setIsTyping(true);

    // Prepare form data if file is uploaded
    let payload;
    let config = {};
    if (file) {
      payload = new FormData();
      payload.append("query", query);
      payload.append("file", file);
      config.headers = { "Content-Type": "multipart/form-data" };
    } else {
      payload = { query };
    }

    // Add user's message to chat
    setChatHistory([
      ...chatHistory,
      { user: query, bot: null, file: file ? URL.createObjectURL(file) : null },
    ]);
    setQuery("");
    setFile(null);

    try {
      const res = await axios.post(
        "http://localhost:5000/chat",
        payload,
        config
      );
      setTimeout(() => {
        // Add bot's response after typing indicator
        setChatHistory((prev) => {
          const newHistory = [...prev];
          const lastIndex = newHistory.length - 1;
          newHistory[lastIndex].bot = res.data.response;
          return newHistory;
        });
        setIsTyping(false);
        setLoading(false);
      }, 900); // simulate typing
    } catch (e) {
      setError("Failed to get response. Try again!");
      setIsTyping(false);
      setLoading(false);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter" && !loading) handleQuery();
  };

  const handleFileChange = (e) => {
    if (e.target.files[0]) setFile(e.target.files[0]);
  };

  return (
    <ThemeProvider theme={darkMode ? darkTheme : lightTheme}>
      <Box
        sx={{
          background: darkMode ? "#191c24" : "#f7f9fa",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          transition: "background 0.4s",
        }}
      >
        <Paper
          elevation={7}
          sx={{
            p: 3,
            maxWidth: 480,
            width: "100%",
            borderRadius: 3,
            boxShadow: 7,
            position: "relative",
          }}
        >
          <Box
            sx={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              mb: 2,
            }}
          >
            <Typography variant="h5">AI Customer Support</Typography>
            <Box sx={{ display: "flex", alignItems: "center", gap: 1 }}>
              <ImageIcon fontSize="small" color="disabled" />
              <Switch
                checked={darkMode}
                onChange={() => setDarkMode((v) => !v)}
                color="default"
              />
            </Box>
          </Box>
          <Box
            sx={{
              height: 340,
              overflowY: "auto",
              mb: 2,
              p: 2,
              background: darkMode ? "#22263a" : "#fff",
              borderRadius: 2,
              border: "1px solid #eee",
              display: "flex",
              flexDirection: "column",
              gap: 1.5,
              transition: "background 0.4s",
            }}
          >
            {chatHistory.map((chat, idx) => (
              <Box
                key={idx}
                sx={{
                  display: "flex",
                  flexDirection: "column",
                  alignItems: "flex-start",
                  gap: 0.5,
                }}
              >
                {/* User bubble */}
                <Box sx={{ display: "flex", alignItems: "flex-end", gap: 1 }}>
                  <Avatar>
                    <AccountCircleIcon />
                  </Avatar>
                  <Box
                    sx={{
                      background: darkMode ? "#0b57d0" : "#e3f2fd",
                      p: 1,
                      borderRadius: 2,
                      maxWidth: 300,
                      wordBreak: "break-word",
                    }}
                  >
                    <Typography fontWeight="bold">You:</Typography>
                    <Typography>{chat.user}</Typography>
                    {chat.file && (
                      <Box
                        sx={{
                          mt: 0.5,
                          borderRadius: 2,
                          overflow: "hidden",
                          border: "1px solid #888",
                          maxWidth: 180,
                        }}
                      >
                        <img
                          src={chat.file}
                          alt="user upload"
                          width="100%"
                          style={{ borderRadius: 8 }}
                        />
                      </Box>
                    )}
                  </Box>
                </Box>
                {/* Bot bubble */}
                {chat.bot !== null && (
                  <Box
                    sx={{
                      display: "flex",
                      alignItems: "flex-end",
                      gap: 1,
                      mt: 0.5,
                      alignSelf: "flex-end",
                    }}
                  >
                    <Box
                      sx={{
                        background: darkMode ? "#14bdac" : "#dcedc8",
                        p: 1,
                        borderRadius: 2,
                        maxWidth: 300,
                        wordBreak: "break-word",
                      }}
                    >
                      <Typography fontWeight="bold">Bot:</Typography>
                      <Typography>{chat.bot}</Typography>
                    </Box>
                    <Avatar sx={{ bgcolor: darkMode ? "#14bdac" : "#7ed957" }}>
                      <SmartToyIcon />
                    </Avatar>
                  </Box>
                )}
              </Box>
            ))}
            {/* Typing indicator */}
            {isTyping && (
              <Box
                sx={{
                  display: "flex",
                  alignItems: "center",
                  alignSelf: "flex-end",
                  gap: 1,
                  mt: 0.5,
                }}
              >
                <Box
                  sx={{
                    background: darkMode ? "#14bdac" : "#dcedc8",
                    p: 1,
                    borderRadius: 2,
                  }}
                >
                  <Typography fontWeight="bold">Bot:</Typography>
                  <Typography>
                    <span className="typing">Typing</span>
                    <span className="typing-dot">.</span>
                    <span className="typing-dot">.</span>
                    <span className="typing-dot">.</span>
                  </Typography>
                </Box>
                <Avatar sx={{ bgcolor: darkMode ? "#14bdac" : "#7ed957" }}>
                  <SmartToyIcon />
                </Avatar>
              </Box>
            )}
            <div ref={chatEndRef} />
          </Box>
          <Box
            sx={{
              display: "flex",
              gap: 1,
              alignItems: "center",
              flexWrap: "wrap",
            }}
          >
            <TextField
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              onKeyDown={handleKeyDown}
              placeholder="Type your question..."
              fullWidth
              size="small"
              disabled={loading}
              sx={{ flexGrow: 1 }}
            />
            <input
              type="file"
              accept="image/*"
              style={{ display: "none" }}
              id="upload-file"
              onChange={handleFileChange}
              disabled={loading}
            />
            <label htmlFor="upload-file">
              <IconButton component="span" disabled={loading}>
                <ImageIcon />
              </IconButton>
            </label>
            <Button
              variant="contained"
              onClick={handleQuery}
              disabled={(!query.trim() && !file) || loading}
              endIcon={
                loading ? <CircularProgress size={18} /> : <SendIcon />
              }
            >
              Send
            </Button>
          </Box>
          {error && (
            <Typography color="error" sx={{ mt: 1 }}>
              {error}
            </Typography>
          )}
        </Paper>
        <style>
          {`
            .typing-dot {
              animation: blink 1.4s infinite both;
            }
            @keyframes blink {
              0%, 80%, 100% { opacity: 0; }
              40% { opacity: 1; }
            }
          `}
        </style>
      </Box>
    </ThemeProvider>
  );
};

