import React, { useState, useRef, useEffect } from "react";
import axios from "axios";
import "./Chatbot.css";

export default function Chatbot() {
  const [open, setOpen] = useState(false);
  const [query, setQuery] = useState("");
  const [chat, setChat] = useState([]);
  const [loading, setLoading] = useState(false);
  const chatEndRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [chat, loading]);

  const send = async () => {
    if (!query.trim()) return;
    setChat([...chat, { sender: "user", text: query }]);
    setQuery("");
    setLoading(true);
    try {
      const res = await axios.post("http://localhost:5000/chat", { query });
      setChat((prev) => [...prev, { sender: "bot", text: res.data.response }]);
    } catch {
      setChat((prev) => [
        ...prev,
        { sender: "bot", text: "Sorry, something went wrong." },
      ]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      {!open && (
        <button className="chat-launch" onClick={() => setOpen(true)}>
          <span role="img" aria-label="chat">ðŸ’¬</span>
        </button>
      )}

      {open && (
        <div className="chat-widget">
          <div className="chat-header">
            <span className="chat-logo" role="img" aria-label="logo">ðŸ’¬</span>
            <span className="chat-title">Support Chat</span>
            <button className="chat-close" onClick={() => setOpen(false)}>
              Ã—
            </button>
          </div>
          <div className="chat-history">
            {chat.map((msg, idx) => (
              <div key={idx} className={`chat-msg chat-${msg.sender}`}>
                {msg.text}
              </div>
            ))}
            {loading && (
              <div className="chat-msg chat-bot">Support is typingâ€¦</div>
            )}
            <div ref={chatEndRef} />
          </div>
          <div className="chat-input-row">
            <input
              value={query}
              placeholder="Type your questionâ€¦"
              onChange={e => setQuery(e.target.value)}
              onKeyDown={e => e.key === "Enter" && send()}
              disabled={loading}
            />
            <button
              onClick={send}
              disabled={!query.trim() || loading}
              className="chat-send"
            >â†’</button>
          </div>
        </div>
      )}
    </div>
  );
}
