import React, { useState, useRef, useEffect } from "react";
import axios from "axios";
import "./Chatbot.css"; // We'll create this CSS next

export default function Chatbot() {
  const [query, setQuery] = useState("");
  const [chatHistory, setChatHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const chatEndRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [chatHistory, loading]);

  const handleQuery = async () => {
    if (!query.trim()) return;
    setLoading(true);
    setChatHistory([...chatHistory, { sender: "user", text: query, timestamp: new Date() }]);
    setQuery("");
    try {
      const res = await axios.post("http://localhost:5000/chat", { query });
      setChatHistory(prev => [
        ...prev,
        { sender: "bot", text: res.data.response, timestamp: new Date() },
      ]);
    } catch {
      setChatHistory(prev => [
        ...prev,
        { sender: "bot", text: "Sorry, something went wrong.", timestamp: new Date() },
      ]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="cs-container">
      <div className="cs-header">
        <span role="img" aria-label="support">ðŸ’¬</span> Customer Support Chatbot
      </div>
      <div className="cs-history">
        {chatHistory.map((msg, idx) => (
          <div className={`cs-message-card ${msg.sender}`} key={idx}>
            <div className="cs-message-text">{msg.text}</div>
            <div className="cs-timestamp">
              {msg.sender === "user" ? "You" : "Support"} â€¢{" "}
              {msg.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
            </div>
          </div>
        ))}
        {loading && (
          <div className="cs-message-card bot">
            <div className="cs-message-text">Support is typingâ€¦</div>
          </div>
        )}
        <div ref={chatEndRef} />
      </div>
      <div className="cs-input-row">
        <input
          type="text"
          value={query}
          placeholder="Type your questionâ€¦"
          onChange={e => setQuery(e.target.value)}
          onKeyDown={e => e.key === "Enter" && handleQuery()}
          disabled={loading}
        />
        <button onClick={handleQuery} disabled={!query.trim() || loading}>
          Send
        </button>
      </div>
    </div>
  );
}
