import React, { useState, useRef, useEffect } from "react";
import "./Chatbot.css";

const quickReplies = [
  "Show my wallet balance",
  "Send â‚¹200 to Arjun",
  "Whatâ€™s my last transaction?",
  "List my subscriptions"
];

export default function Chatbot() {
  const [query, setQuery] = useState("");
  const [messages, setMessages] = useState([
    { sender: "bot", text: "ðŸ‘‹ Hi! I'm Wallet AI. How can I help you?" },
    { sender: "user", text: "Show me my recent transactions." },
    { sender: "bot", text: "ðŸª™ You spent â‚¹800 at Flipkart. Anything else?" }
  ]);
  const [loading, setLoading] = useState(false);
  const chatEndRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages, loading]);

  const send = msg => {
    const text = msg || query;
    if (!text.trim()) return;
    setMessages([...messages, { sender: "user", text }]);
    setQuery("");
    setLoading(true);
    setTimeout(() => {
      setMessages(m => [
        ...m,
        { sender: "bot", text: "âœ¨ (AI) This is a sample Wallet AI reply." }
      ]);
      setLoading(false);
    }, 900);
  };

  return (
    <div className="wallet-bg-full">
      <div className="wallet-fullscreen-container">
        <div className="wallet-header-full">
          <div className="header-avatar-glass">
            <svg width="44" height="44" viewBox="0 0 44 44">
              <defs>
                <radialGradient id="starGrad" cx="50%" cy="50%" r="70%">
                  <stop offset="0%" stopColor="#6D6AFF"/>
                  <stop offset="100%" stopColor="#21D4FD"/>
                </radialGradient>
              </defs>
              <circle cx="22" cy="22" r="22" fill="url(#starGrad)" />
              <g>
                <polygon points="22,10 24,19 34,19 25,24 27,34 22,28 17,34 19,24 10,19 20,19"
                  fill="#fff" opacity="0.96"/>
              </g>
            </svg>
          </div>
          <span className="wallet-title-glass">Wallet AI</span>
        </div>
        <div className="wallet-chat-scrollarea">
          <div className="wallet-chat-history-glass">
            {messages.map((msg, idx) => (
              <div key={idx} className={`wallet-bubble-glass ${msg.sender}`}>
                {msg.sender === "bot" && (
                  <span className="wallet-avatar-glass">
                    <img
                      src="https://cdn-icons-png.flaticon.com/512/4712/4712039.png"
                      alt="bot"
                    />
                  </span>
                )}
                <span className="wallet-bubble-text">{msg.text}</span>
                {msg.sender === "user" && (
                  <span className="wallet-avatar-glass user">
                    <img
                      src="https://cdn-icons-png.flaticon.com/512/9131/9131529.png"
                      alt="user"
                    />
                  </span>
                )}
              </div>
            ))}
            {loading && (
              <div className="wallet-bubble-glass bot">
                <span className="wallet-avatar-glass">
                  <img
                    src="https://cdn-icons-png.flaticon.com/512/4712/4712039.png"
                    alt="bot"
                  />
                </span>
                <span className="wallet-bubble-text">Typing...</span>
              </div>
            )}
            <div ref={chatEndRef} />
          </div>
          <div className="wallet-quick-replies">
            {quickReplies.map((txt, i) => (
              <button
                key={i}
                className="wallet-chip-glass"
                onClick={() => send(txt)}
              >
                {txt}
              </button>
            ))}
          </div>
        </div>
        <div className="wallet-input-row-glass wallet-input-row-full">
          <input
            type="text"
            placeholder="Ask Wallet AIâ€¦"
            value={query}
            onChange={e => setQuery(e.target.value)}
            onKeyDown={e => e.key === "Enter" && send()}
          />
          <button
            className="wallet-send-btn-glass"
            onClick={() => send()}
            disabled={!query.trim() || loading}
          >
            <svg width="27" height="27" viewBox="0 0 26 26">
              <defs>
                <linearGradient id="wallet-send-grad2" x1="0%" y1="0%" x2="100%" y2="100%">
                  <stop offset="0%" stopColor="#21D4FD"/>
                  <stop offset="100%" stopColor="#6D6AFF"/>
                </linearGradient>
              </defs>
              <circle cx="13" cy="13" r="13" fill="url(#wallet-send-grad2)" />
              <polygon points="9,7 19,13 9,19 11,13" fill="#fff"/>
            </svg>
          </button>
        </div>
      </div>
    </div>
  );
}
