import React, { useState, useRef, useEffect } from "react";
import "./Chatbot.css";

const quickReplies = [
  "Check my balance",
  "Send â‚¹500 to Manu",
  "Show last 5 transactions",
  "List active subscriptions"
];

export default function Chatbot() {
  const [query, setQuery] = useState("");
  const [messages, setMessages] = useState([
    { sender: "bot", text: "ðŸ‘‹ Hi! Iâ€™m Wallet AI. How can I help you?" },
    { sender: "user", text: "Show me my recent transactions." },
    { sender: "bot", text: "ðŸª™ You spent â‚¹800 at Flipkart. Anything else?" }
  ]);
  const [loading, setLoading] = useState(false);
  const chatEndRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages, loading]);

  const send = msg => {
    const text = msg || query;
    if (!text.trim()) return;
    setMessages([...messages, { sender: "user", text }]);
    setQuery("");
    setLoading(true);
    setTimeout(() => {
      setMessages(m => [
        ...m,
        { sender: "bot", text: "âœ… (AI) Sample Wallet AI reply." }
      ]);
      setLoading(false);
    }, 700);
  };

  return (
    <div className="apple-bg">
      <div className="apple-chat-container">
        <div className="apple-header">
          <span role="img" aria-label="wallet" className="apple-header-emoji">ðŸ‘›</span>
          <span className="apple-title">Wallet AI</span>
        </div>
        <div className="apple-chat-main">
          <div className="apple-chat-history">
            {messages.map((msg, idx) => (
              <div
                key={idx}
                className={`apple-bubble ${msg.sender}`}
              >
                {msg.text}
              </div>
            ))}
            {loading && (
              <div className="apple-bubble bot">
                Typingâ€¦
              </div>
            )}
            <div ref={chatEndRef} />
          </div>
          <div className="apple-quick-replies">
            {quickReplies.map((txt, i) => (
              <button
                key={i}
                className="apple-chip"
                onClick={() => send(txt)}
              >
                {txt}
              </button>
            ))}
          </div>
        </div>
        <div className="apple-input-row">
          <input
            type="text"
            placeholder="Ask Wallet AIâ€¦"
            value={query}
            onChange={e => setQuery(e.target.value)}
            onKeyDown={e => e.key === "Enter" && send()}
          />
          <button
            className="apple-send-btn"
            onClick={() => send()}
            disabled={!query.trim() || loading}
          >
            <svg width="24" height="24" viewBox="0 0 22 22">
              <circle cx="11" cy="11" r="11" fill="#007aff" />
              <polygon points="7,5 17,11 7,17 9,11" fill="#fff"/>
            </svg>
          </button>
        </div>
      </div>
    </div>
  );
}
