import React, { useState, useRef, useEffect } from "react";
import "./Chatbot.css";

export default function Chatbot() {
  const [query, setQuery] = useState("");
  const [messages, setMessages] = useState([
    { sender: "bot", text: "Hey! ðŸ‘‹ Iâ€™m Wallet AI. How can I help you today?" },
    { sender: "user", text: "Show me my recent transactions." },
    { sender: "bot", text: "Here are your latest 3 transactions..." }
  ]);
  const chatEndRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const send = () => {
    if (!query.trim()) return;
    setMessages([...messages, { sender: "user", text: query }]);
    setQuery("");
    setTimeout(() => {
      setMessages(m =>
        [...m, { sender: "bot", text: "AI Bot is processing your request..." }]
      );
    }, 900);
  };

  return (
    <div className="wallet-bg">
      <div className="wallet-chat-card">
        <div className="wallet-header">Wallet AI</div>
        <div className="wallet-chat-history">
          {messages.map((msg, idx) =>
            <div key={idx} className={`wallet-bubble ${msg.sender}`}>
              {msg.sender === "bot" && (
                <span className="wallet-avatar">
                  <img src="https://cdn-icons-png.flaticon.com/512/4712/4712039.png" alt="bot" />
                </span>
              )}
              <span>{msg.text}</span>
              {msg.sender === "user" && (
                <span className="wallet-avatar user">
                  <img src="https://cdn-icons-png.flaticon.com/512/9131/9131529.png" alt="user" />
                </span>
              )}
            </div>
          )}
          <div ref={chatEndRef} />
        </div>
        <div className="wallet-input-row">
          <input
            type="text"
            placeholder="Ask Wallet AIâ€¦"
            value={query}
            onChange={e => setQuery(e.target.value)}
            onKeyDown={e => e.key === "Enter" && send()}
          />
          <button className="wallet-send-btn" onClick={send} disabled={!query.trim()}>
            <svg width="26" height="26" viewBox="0 0 26 26">
              <defs>
                <linearGradient id="wallet-send-grad" x1="0%" y1="0%" x2="100%" y2="100%">
                  <stop offset="0%" stopColor="#23B6E0"/>
                  <stop offset="100%" stopColor="#6F4EF2"/>
                </linearGradient>
              </defs>
              <circle cx="13" cy="13" r="13" fill="url(#wallet-send-grad)" />
              <polygon points="9,7 19,13 9,19 11,13" fill="#fff"/>
            </svg>
          </button>
        </div>
      </div>
    </div>
  );
}
