import React, { useState, useRef, useEffect } from "react";
import "./Chatbot.css";

const quickReplies = [
  "Reset my password",
  "I have a billing question",
  "Report an issue",
  "Connect me to an agent"
];

export default function Chatbot() {
  const [query, setQuery] = useState("");
  const [messages, setMessages] = useState([
    { sender: "bot", text: "ðŸ‘‹ Welcome! How can I assist you today?" },
    { sender: "user", text: "I need help with my account." },
    { sender: "bot", text: "Of course! Can you tell me a bit more?" }
  ]);
  const [loading, setLoading] = useState(false);
  const chatEndRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages, loading]);

  const send = msg => {
    const text = msg || query;
    if (!text.trim()) return;
    setMessages([...messages, { sender: "user", text }]);
    setQuery("");
    setLoading(true);
    setTimeout(() => {
      setMessages(m => [
        ...m,
        { sender: "bot", text: "ðŸŸ¦ Thank you! Our support team will respond soon." }
      ]);
      setLoading(false);
    }, 800);
  };

  return (
    <div className="cs-bg">
      <header className="cs-header">
        <span className="cs-header-emoji" role="img" aria-label="support">ðŸ’¬</span>
        <span className="cs-header-title">Customer Support Bot</span>
      </header>
      <main className="cs-main">
        <div className="cs-chat-history">
          {messages.map((msg, idx) => (
            <div
              key={idx}
              className={`cs-bubble ${msg.sender}`}
            >
              {msg.text}
            </div>
          ))}
          {loading && (
            <div className="cs-bubble bot">
              Typingâ€¦
            </div>
          )}
          <div ref={chatEndRef} />
        </div>
        <div className="cs-quick-replies">
          {quickReplies.map((txt, i) => (
            <button
              key={i}
              className="cs-chip"
              onClick={() => send(txt)}
            >
              {txt}
            </button>
          ))}
        </div>
      </main>
      <footer className="cs-input-row">
        <input
          type="text"
          placeholder="Type your question..."
          value={query}
          onChange={e => setQuery(e.target.value)}
          onKeyDown={e => e.key === "Enter" && send()}
        />
        <button
          className="cs-send-btn"
          onClick={() => send()}
          disabled={!query.trim() || loading}
        >
          <svg width="24" height="24" viewBox="0 0 22 22">
            <circle cx="11" cy="11" r="11" fill="#007aff" />
            <polygon points="7,5 17,11 7,17 9,11" fill="#fff"/>
          </svg>
        </button>
      </footer>
    </div>
  );
}
