import React, { useState, useRef, useEffect } from "react";
import "./Chatbot.css";

export default function Chatbot() {
  const [query, setQuery] = useState("");
  const [messages, setMessages] = useState([
    {
      sender: "bot",
      type: "text",
      text: "Based on recent data, the spike in traffic is primarily from increased referrals from Social Media (45%) and Organic Search (35%). Bounce rate on the homepage has dropped by 10% compared to last week."
    },
    {
      sender: "bot",
      type: "file",
      filename: "Example File",
      desc: "Downloads",
      size: "500 KB"
    },
    {
      sender: "user",
      type: "text",
      text: "Can you export this as a CSV?"
    },
    {
      sender: "bot",
      type: "text",
      text: "Analyzing data, please wait..."
    }
  ]);
  const [loading, setLoading] = useState(false);
  const chatEndRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const aiSuggestions = [
    "Write an email to the marketing team",
    "Summarize key points",
    "Create a follow-up checklist"
  ];

  const [selected, setSelected] = useState(1);

  const send = () => {
    if (!query.trim()) return;
    setMessages([...messages, { sender: "user", type: "text", text: query }]);
    setQuery("");
    setLoading(true);
    setTimeout(() => {
      setMessages(m =>
        [...m, { sender: "bot", type: "text", text: "This is a sample AI response." }]
      );
      setLoading(false);
    }, 1000);
  };

  return (
    <div className="ai-bg">
      <div className="ai-center-box">
        <div className="ai-sparkle">
          {/* Replace this emoji with your SVG icon if available */}
          <span role="img" aria-label="sparkle">‚ú®</span>
        </div>
        <div className="ai-suggest-label">Asking with AI Suggestions</div>
        <div className="ai-suggestions-row">
          {aiSuggestions.map((s, i) => (
            <div
              key={i}
              className={`ai-suggestion-chip ${selected === i ? "selected" : ""}`}
              onClick={() => setSelected(i)}
            >
              {s}
            </div>
          ))}
        </div>
        <div className="ai-chat-history">
          {messages.map((msg, idx) =>
            msg.type === "file" ? (
              <div key={idx} className="ai-bubble bot file">
                {/* Replace emoji with your file SVG */}
                <div className="ai-file-card">
                  <span className="ai-file-icon" role="img" aria-label="file">üìÑ</span>
                  <div>
                    <div className="ai-file-name">{msg.filename}</div>
                    <div className="ai-file-desc">{msg.desc}</div>
                    <div className="ai-file-size">{msg.size}</div>
                  </div>
                </div>
              </div>
            ) : (
              <div
                key={idx}
                className={`ai-bubble ${msg.sender}`}
              >
                {msg.sender === "bot" && (
                  <span className="ai-bot-avatar" role="img" aria-label="bot">ü§ñ</span>
                )}
                <span>{msg.text}</span>
              </div>
            )
          )}
          {loading && (
            <div className="ai-bubble bot">
              <span className="ai-bot-avatar" role="img" aria-label="bot">ü§ñ</span>
              <span>Typing...</span>
            </div>
          )}
          <div ref={chatEndRef} />
        </div>
        <div className="ai-input-row">
          <input
            type="text"
            placeholder="Ask, write or search for anything..."
            value={query}
            onChange={e => setQuery(e.target.value)}
            onKeyDown={e => e.key === "Enter" && send()}
            disabled={loading}
          />
          <button onClick={send} disabled={!query.trim() || loading} className="ai-send-btn">
            {/* Replace with your send SVG icon */}
            <span role="img" aria-label="send">‚û°Ô∏è</span>
          </button>
        </div>
      </div>
    </div>
  );
}
